class Team:
    wins = 0
    losses = 0
    games = 0
    results = {}

    def points(self):
        return self.wins * 3  # Assuming 3 points for a win, you can adjust as needed
    
    def record(self):
        return f"{self.wins} Wins, {self.losses} Losses"
    
    def win(self):
        self.wins += 1
        self.games += 1
    
    def loss(self):
        self.losses += 1
        self.games += 1
    
    def __init__(self):
        pass

